# Combinar las matrices RGB
I_RGB(:,:,1) = IM_R;
I_RGB(:,:,2) = IM_G;
I_RGB(:,:,3) = IM_B;
I_RGB = uint8(I_RGB);

# Obtener dimensiones
[r c nm] = size(I_RGB);
printf('Dimensiones de la imagen: %d x %d x %d\n', r, c, nm);

# Mostrar la imagen
figure(1)
imshow(I_RGB);
title('Imagen RGB - payaso.png', 'fontsize', 14)
xlabel('Píxeles')
axis('on')

# Mostrar imagen original sin escalar
figure(2)
imshow(I_RGB);
title('Imagen Original', 'fontsize', 12)
xlabel(sprintf('Dimensiones: %d x %d píxeles', r, c))
axis('on')
